  margin: 1%;
  border-radius: 20px;

  .label {
    font-size: 2em;    
    margin: .3em 0 0 .6em;
    font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Oxygen, Ubuntu, Cantarell, "Open Sans", "Helvetica Neue", sans-serif;
    opacity: 0.5;
  }
 
}



.board-controls {
  border: 0px solid gray;
   text-align: center; 
 
  .btn-add-task {
    color: #000;
    background-color: #ffffff;
    margin: 0;
    padding: 0 0 0.4rem;
